package pkg02_substitute;

public class MainClass 
{

  public static void main(String[] args) 
  {
    // TODO Auto-generated method stub
    // 대입 연산
    int a = 10;
    System.out.println(a);
    
    // 복합 대입 연산(산술 연산 + 대입 연산)
    int b = 0;
    b += 1;
    System.out.println(b);
    b -= 1;
    System.out.println(b);
    
  }

}
